mapper.select:
SELECT COUNT(*) 
FROM member 
WHERE email = #{email}

    SELECT COUNT(*)
	FROM schedule_category
	WHERE schedule_category_id =#{categoryId}

SELECT s.schedule_id AS scheduleId, s.name, c.schedule_category_id AS scheduleCategoryId, c.schedule_category_name AS scheduleCategoryName, c.schedule_color AS scheduleColor, s.is_group AS isGroup, s.duration, TO_CHAR(s.start_date, 'YYYY-MM-DD HH24:MI') AS startDate, TO_CHAR(s.end_date, 'YYYY-MM-DD HH24:MI') AS endDate, s.place_name AS placeName FROM member_schedule ms JOIN schedule s ON ms.schedule_id = s.schedule_id JOIN schedule_category c ON s.schedule_category_id = c.schedule_category_id WHERE ms.email = #{email}
AND s.is_group = #{isGroup}
AND TO_CHAR(s.start_date, 'YYYY') = #{year} AND TO_CHAR(s.start_date, 'MM') = #{month} AND s.end_date IS NOT NULL AND ( (s.is_group = 1 AND s.place_name IS NOT NULL AND s.place_latitude IS NOT NULL AND s.place_longitude IS NOT NULL) OR s.is_group = 0)
